import React, { Component } from 'react';

import "../css/graphStyle.css"

const Line = ({ left }) => {
    return (
        <div
            className="line"
            style={{ left: '${left}%' }}
        />
    )
}

const BarTextContent = () => {
    return (
        <div className="bar-text-content">
            {/*   
                times.map((timeSpent) => (
                    <div className="text">
                        { timeSpent.Name }
                    </div>
                ))*/
            }
        </div>
    )
}

const Bar = ({ percent }) => {
    return(
        <div className="bar" style={{ width: '${percent}%'}} />
    )
}

class Graph extends Component {
    constructor(props){
    super(props);
        this.state = {

        };
};

renderLines(){
    return Array(10).fill(null).map((el, i) => (
        <Line
            left={i*10}
            key={i}
        />
    ))
}

renderBars(){
    const { times } = this.props;

    let sumOfAllTimes = times.reduce((acc, timeSpent) => {
        return acc + timeSpent; //timeSpent.marketCap
    }, 0);

    return times.map((timeSpent) => {
        const percent = (timeSpent / sumOfAllTimes) * 100; //timeSpent.marketCap
        return(
            <Bar
                percent={percent}
                //key={timeSpent.employee}
            />
        )
    });
}

render(){
    return(
        <div className="graph-wrapper">

            <div className="graph">
                <BarTextContent/>

                <div className="bar-lines-container">
                    { this.renderLines() }
                    { this.renderBars() }
                </div>
            </div>

        </div>
    )
}
}
export default Graph;
